import React from "react";
import { useState } from "react";


function Form(props) {

    let [form, setForm] = useState({})
    let [error, setError] = useState({})

    const handleSubmit = (e) => {
        e.preventDefault()
        console.log(form)
    }

    const setField = (field, value) => {
        setForm({
            ...form, 
            [field]: value
        })

        if(!!error[field]){
            setError({
                ...error, 
                [field]: null
            }) 
        }
    }
    return (
        <div class="form_container">
            <h2>Interested?</h2>
            <span>Submit a bid!</span>
            <form class="container" action="">
                <input type="text" name='username' placeHolder="Username" value={form.username} isInvalid={!!error.username} onChange={(e)=> setForm("username", e.target.value)} required/>
                <input type="text" name='email' placeHolder="Email" onChange={(e)=> setForm("email", e.target.value)}  required/>
                <input type="text" name='phone' placeHolder="Phone number" onChange={(e)=> setForm("phone", e.target.value)}  />
                <input type="text" name='bid' placeHolder="Your bid" onChange={(e)=> setForm("bid", e.target.value)} />
                <button type="submit" onClick={handleSubmit}>Submit your bid</button>
            </form>
        </div>
    );  
  };

  export default Form;
