import '../css/Card.css';
import React from 'react';
import {Link} from 'react-router-dom';

function Card(props) {
  let item = props.item
  return (   
    <Link class="card-container" 
    to={`/details/${props.name}`}
      state={props}
    >
        <div>
            <img src="../assets/us.jpg" alt="Mandolin" />
            <h1>{props.name}</h1>
            <p>{props.description}</p>
            <span>Price:{props.price}</span>
        </div>
    </Link>
  );
}

export default Card;
